:host {
  display: block;
  color: white;
  padding: 2rem 3rem;
}

ul {
  margin-top: 2rem;
  list-style: none;
  padding: 0%;
}

.social-media {
  font-size: 2rem;
  display: flex;
  align-items: center;
  justify-content: space-around;
}

.social-media li {
  height: 60px;
  width: 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  color: var(--green);
  background-color: rgba(255, 255, 255, 0.2);
}

.contact-info li {
  height: 30px;
  font-size: 1.2rem;
}

/* PC */

:host.pc .container {
  display: grid;
  gap: 2rem;
  grid-template-columns: 1fr 1fr;
}

:host.pc h1 {
  grid-column: 1/3;
}

:host.pc .social-media,
:host.pc .contact-info {
  width: 400px;
  margin: 0 auto;
}

:host.pc .social-media {
  cursor: pointer;
}

:host.pc .social-media li:hover {
  background-color: rgba(255, 255, 255, 0.3);
}
